import React, { useState, useEffect, useRef } from 'react';
import itemGroups from './data/items';
import ItemGroup from './components/ItemGroup';

function App() {
  const audioRef = useRef(null);

  // ⏱ Load from localStorage on first render
  const [activeTimers, setActiveTimers] = useState(() => {
    const saved = localStorage.getItem('activeTimers');
    if (!saved) return {};
    const parsed = JSON.parse(saved);
    const now = Date.now();

    // Remove expired timers
    const validTimers = {};
    for (const [id, endTime] of Object.entries(parsed)) {
      if (endTime > now) {
        validTimers[id] = endTime;
      }
    }
    return validTimers;
  });

  // 🔄 Save to localStorage whenever timers change
  useEffect(() => {
    localStorage.setItem('activeTimers', JSON.stringify(activeTimers));
  }, [activeTimers]);

  // 🔔 Timer interval: update every second and play sound if done
  useEffect(() => {
    const interval = setInterval(() => {
      setActiveTimers(prev => {
        const updated = { ...prev };
        const now = Date.now();

        for (const id in prev) {
          if (prev[id] <= now) {
            delete updated[id];
            if (audioRef.current) {
              audioRef.current.play().catch(() => {});
            }
          }
        }

        return updated;
      });
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  // 🔘 Start or cancel a timer
  const toggleTimer = (id, duration) => {
    setActiveTimers(prev => {
      const updated = { ...prev };
      if (updated[id]) {
        delete updated[id];
      } else {
        updated[id] = Date.now() + duration * 1000;
      }
      return updated;
    });
  };

  // ⏳ Get time left in seconds
  const getTimeLeft = (endTime) => {
    if (!endTime) return '';
    const seconds = Math.ceil((endTime - Date.now()) / 1000);
    return seconds > 0 ? `${seconds}s` : 'Done';
  };

  return (
    <div style={{ padding: '20px' }}>
      <audio ref={audioRef} src="/done.mp3" preload="auto" />
      <div style={{ display: 'flex', gap: '40px', alignItems: 'flex-start' }}>
        {Object.entries(itemGroups).map(([groupName, items]) => (
          <ItemGroup
            key={groupName}
            groupName={groupName}
            items={items}
            activeTimers={activeTimers}
            toggleTimer={toggleTimer}
            getTimeLeft={getTimeLeft}
          />
        ))}
      </div>
    </div>
  );
}

export default App;
