import { useState, useEffect } from 'react';

const items = [
  { id: 1, name: 'Iron Sword', image: '/images/sword.png', duration: 5 }, // in seconds
  { id: 2, name: 'Magic Wand', image: '/images/wand.png', duration: 10 },
  { id: 3, name: 'Health Potion', image: '/images/potion.png', duration: 8 }
];

function App() {
  const [activeTimers, setActiveTimers] = useState({});

  const startTimer = (id, duration) => {
    if (activeTimers[id]) return; // already active

    const endTime = Date.now() + duration * 1000;
    setActiveTimers(prev => ({ ...prev, [id]: endTime }));
  };

  useEffect(() => {
    const interval = setInterval(() => {
      setActiveTimers(prev => {
        const updated = { ...prev };
        const now = Date.now();
        for (const id in updated) {
          if (updated[id] <= now) {
            delete updated[id];
          }
        }
        return updated;
      });
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  const getTimeLeft = (endTime) => {
    const seconds = Math.ceil((endTime - Date.now()) / 1000);
    return seconds > 0 ? `${seconds}s` : 'Done';
  };

  return (
    <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 120px)', gap: '16px', padding: '20px' }}>
      {items.map(item => {
        const isActive = !!activeTimers[item.id];
        return (
          <div
            key={item.id}
            onClick={() => startTimer(item.id, item.duration)}
            style={{
              width: '120px',
              height: '120px',
              position: 'relative',
              cursor: isActive ? 'not-allowed' : 'pointer',
              opacity: isActive ? 0.5 : 1,
              border: '2px solid #333',
              borderRadius: '8px',
              overflow: 'hidden',
              backgroundColor: '#eee',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}
          >
            <img src={item.image} alt={item.name} style={{ width: '80px', height: '80px' }} />
            {isActive && (
              <div
                style={{
                  position: 'absolute',
                  top: 0,
                  left: 0,
                  width: '100%',
                  height: '100%',
                  backgroundColor: 'rgba(0,0,0,0.5)',
                  color: 'white',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontWeight: 'bold',
                  fontSize: '18px'
                }}
              >
                {getTimeLeft(activeTimers[item.id])}
              </div>
            )}
          </div>
        );
      })}
    </div>
  );
}

export default App;
