import React, { useState, useEffect } from "react";

function App() {
  const [timers, setTimers] = useState([]);
  const [itemName, setItemName] = useState("");
  const [minutes, setMinutes] = useState("");

  const addTimer = () => {
    if (!itemName || isNaN(minutes) || minutes <= 0) return alert("Enter item and valid time.");

    const newTimer = {
      id: Date.now(),
      name: itemName,
      endTime: Date.now() + minutes * 60000
    };

    setTimers((prev) => [...prev, newTimer]);
    setItemName("");
    setMinutes("");
  };

  const removeTimer = (id) => {
    setTimers((prev) => prev.filter((timer) => timer.id !== id));
  };

  useEffect(() => {
    const interval = setInterval(() => {
      setTimers((prev) =>
        prev.map((timer) => ({ ...timer })) // Trigger rerender every second
      );
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  const formatTime = (endTime) => {
    const remaining = endTime - Date.now();
    if (remaining <= 0) return "Done! ðŸŽ‰";

    const mins = Math.floor(remaining / 60000);
    const secs = Math.floor((remaining % 60000) / 1000);
    return `${mins}m ${secs}s`;
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>Fantasy Online 2 Crafting Timer</h1>
      <input
        type="text"
        placeholder="Item name"
        value={itemName}
        onChange={(e) => setItemName(e.target.value)}
      />
      <input
        type="number"
        placeholder="Minutes"
        value={minutes}
        onChange={(e) => setMinutes(e.target.value)}
      />
      <button onClick={addTimer}>Start Timer</button>

      <div style={{ marginTop: "20px" }}>
        {timers.map((timer) => (
          <div
            key={timer.id}
            style={{
              border: "1px solid #aaa",
              padding: "10px",
              marginBottom: "10px",
              borderRadius: "6px"
            }}
          >
            <strong>{timer.name}</strong> - {formatTime(timer.endTime)}{" "}
            {Date.now() >= timer.endTime && (
              <button onClick={() => removeTimer(timer.id)}>Remove</button>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
