import React from 'react';
import ItemGroup from './ItemGroup';
import '../styles.css'; // or wherever your CSS is

const App = ({ groupedItems, activeTimers, toggleTimer, getTimeLeft }) => {
  return (
    <div className="all-groups">
      {Object.entries(groupedItems).map(([groupName, items], index) => (
        <ItemGroup
          key={groupName}
          groupName={groupName}
          items={items}
          activeTimers={activeTimers}
          toggleTimer={toggleTimer}
          getTimeLeft={getTimeLeft}
          className={groupName === 'T4' ? 'break-row' : ''}
        />
      ))}
    </div>
  );
};

export default App;
