import '../styles.css';
const Form = ({ customMinutes, setCustomMinutes, customSeconds, setCustomSeconds, onSubmit, onCancel }) => {
  return (
    <>
      <h3>Set Custom Timer</h3>
      <div className="modal-inputs">
        <input
          type="number"
          placeholder="Minutes"
          value={customMinutes}
          onChange={e => setCustomMinutes(e.target.value)}
          style={{ marginRight: '8px', width: '100px' }}
        />
        <input
          type="number"
          placeholder="Seconds"
          value={customSeconds}
          onChange={e => setCustomSeconds(e.target.value)}
          style={{ width: '100px' }}
        />
      </div>
      <div className="modal-buttons" style={{ marginTop: '12px' }}>
        <button onClick={onSubmit} style={{ marginRight: '8px' }}>Start</button>
        <button onClick={onCancel}>Cancel</button>
      </div>
    </>
  );
};

export default Form;
