import '../styles.css';

const Form = ({ customMinutes, setCustomMinutes, customSeconds, setCustomSeconds, onSubmit, onCancel }) => {
  const handleSubmit = (e) => {
    e.preventDefault(); // prevent page reload
    onSubmit();
  };

  return (
    <div className='form-container'>
      <form onSubmit={handleSubmit}>
        <label className='label-container'>
          <div className='label-group'>
            <div className='item-card'>Item</div>
            <div className='label-title'>Minutes:</div>
          </div>
          <input
            className='form-input'
            type="number"
            min="0"
            value={customMinutes}
            onChange={(e) => setCustomMinutes(e.target.value)}
          />
        </label>

        <label className='label-container'>
          <div className='label-group'>
            <div className='item-card' /> {/* Empty or you can put seconds icon */}
            <div className='label-title'>Seconds:</div>
          </div>
          <input
            className='form-input'
            type="number"
            min="0"
            max="59"
            value={customSeconds}
            onChange={(e) => setCustomSeconds(e.target.value)}
          />
        </label>

        <div className='input-group'>
          <button className='btn' type="submit">Submit</button>
          <button className='btn' type="button" onClick={onCancel}>Cancel</button>
        </div>
      </form>
    </div>
  );
};

export default Form;
