import '../styles.css';

const Form = ({   
    customDays,
    setCustomDays,
    customHours,
    setCustomHours,
    customMinutes,
    setCustomMinutes,
    customSeconds,
    setCustomSeconds,
    onSubmit,
    onCancel }) => {
  const handleSubmit = (e) => {
    e.preventDefault(); // prevent page reload
    onSubmit();
  };

  return (
    <div className='form-container'>
      <form onSubmit={handleSubmit}>

        <label className='label-container'>

            {/* ItemCard */}
            {item && (
                <div className="item-info">
                <img src={item.image} alt={item.name} style={{ width: 50, height: 50 }} />
                <div>{item.name}</div>
                </div>
            )}

          <div className='label-group'>
            <div className='label-title'>Days:</div>
            <input
                className='form-input'
                type="number"
                min="0"
                value={customDays}
                onChange={(e) => setCustomDays(e.target.value)}
            />
          </div>

          <div className='label-group'>
            <div className='label-title'>Hours:</div>
            <input
                className='form-input'
                type="number"
                min="0"
                max="23"
                value={customHours}
                onChange={(e) => setCustomHours(e.target.value)}
            />
          </div>

          <div className='label-group'>
            <div className='label-title'>Minutes:</div>
            <input
                className='form-input'
                type="number"
                min="0"
                max="59"
                value={customMinutes}
                onChange={(e) => setCustomMinutes(e.target.value)}
            />
          </div>

          <div className='label-group'>
            <div className='label-title'>Seconds:</div>
            <input
                className='form-input'
                type="number"
                min="0"
                max="59"
                value={customSeconds}
                onChange={(e) => setCustomSeconds(e.target.value)}
            />
          </div>

        </label>


        <div className='input-group'>
          <button className='btn' type="submit">Submit</button>
          <button className='btn' type="button" onClick={onCancel}>Cancel</button>
        </div>
      </form>
    </div>
  );
};

export default Form;
