import React from 'react';

const ItemCard = ({ item, isActive, onClick, timeLeft }) => {
  return (
    <div
      onClick={onClick}
      style={{
        width: '32px',
        height: '32px',
        position: 'relative',
        cursor: 'pointer',
        opacity: isActive ? 0.5 : 1,
        border: '2px solid #333',
        borderRadius: '8px',
        overflow: 'hidden',
        backgroundColor: '#eee',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}
    >
      <img src={item.image} alt={item.name} style={{ width: '60px', height: '60px' }} />
      {isActive && (
        <div
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            backgroundColor: 'rgba(0,0,0,0.5)',
            color: 'white',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontWeight: 'bold',
            fontSize: '18px'
          }}
        >
          {timeLeft}
        </div>
      )}
    </div>
  );
};

export default ItemCard;
