import ItemGroup from './ItemGroup';

import itemGroups from '../data/items'; // Adjust path if needed
import '../styles.css';

const ItemGroupList = ({ activeTimers, toggleTimer, getTimeLeft }) => {
  return (
<div className="all-groups-rows">
  {/* Row 1: T1 and T2 */}
  <div className="tier-row">
    {['T1', 'T2'].map(tierKey => (
      <div key={tierKey} className="tier-section">
        <h1 className="tier-title">{tierKey}</h1>
        <div className="groups-row">
          {Object.entries(itemGroups[tierKey]).map(([groupName, items]) => (
            <div key={groupName} className="item-group">
              <h2 className="group-name">{groupName}</h2>
              <div className="items-list">
                {items.map(item => (
                  <ItemCard key={item.id} item={item} />
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>
    ))}
  </div>

  {/* Row 2: T3 and T4 */}
  <div className="tier-row">
    {['T3', 'T4'].map(tierKey => (
      <div key={tierKey} className="tier-section">
        <h1 className="tier-title">{tierKey}</h1>
        <div className="groups-row">
          {Object.entries(itemGroups[tierKey]).map(([groupName, items]) => (
            <div key={groupName} className="item-group">
              <h2 className="group-name">{groupName}</h2>
              <div className="items-list">
                {items.map(item => (
                  <ItemCard key={item.id} item={item} />
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>
    ))}
  </div>
</div>


  );
};

export default ItemGroupList;
