// ... imports

function App() {
  const [activeTimers, setActiveTimers] = useState({});
  const audioRef = useRef(null);

  // Load timers on mount
  useEffect(() => {
    const storedTimers = JSON.parse(localStorage.getItem('activeTimers') || '{}');
    const now = Date.now();
    const validTimers = {};

    for (const [id, endTime] of Object.entries(storedTimers)) {
      if (endTime > now) {
        validTimers[id] = endTime;
      }
    }

    setActiveTimers(validTimers);
  }, []);

  // Save timers on change
  useEffect(() => {
    localStorage.setItem('activeTimers', JSON.stringify(activeTimers));
  }, [activeTimers]);

  useEffect(() => {
    const interval = setInterval(() => {
      setActiveTimers(prev => {
        const updated = { ...prev };
        const now = Date.now();

        for (const id in prev) {
          if (prev[id] <= now) {
            delete updated[id];
            if (audioRef.current) {
              audioRef.current.play().catch(() => {});
            }
          }
        }

        return updated;
      });
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  const toggleTimer = (id, duration) => {
    setActiveTimers(prev => {
      const updated = { ...prev };
      if (updated[id]) {
        delete updated[id];
      } else {
        updated[id] = Date.now() + duration * 1000;
      }
      return updated;
    });
  };

  const getTimeLeft = (endTime) => {
    if (!endTime) return '';
    const seconds = Math.ceil((endTime - Date.now()) / 1000);
    return seconds > 0 ? `${seconds}s` : 'Done';
  };

  return (
    <div style={{ padding: '20px' }}>
      <audio ref={audioRef} src="/done.mp3" preload="auto" />
      <div style={{ display: 'flex', gap: '40px', alignItems: 'flex-start' }}>
        {Object.entries(itemGroups).map(([groupName, items]) => (
          <ItemGroup
            key={groupName}
            groupName={groupName}
            items={items}
            activeTimers={activeTimers}
            toggleTimer={toggleTimer}
            getTimeLeft={getTimeLeft}
          />
        ))}
      </div>
    </div>
  );
}
