import React, { useState, useEffect, useRef } from 'react';
import Header from './components/Header';
import SideBar from './components/SideBar';
import ItemGroupList from './components/ItemGroupList';
import Footer from './components/Footer';
import './styles.css';


function App() {
  const [modalOpen, setModalOpen] = useState(false);
  const [currentItemId, setCurrentItemId] = useState(null);

  const audioRef = useRef(null);

  // Load from localStorage on first render
  const [activeTimers, setActiveTimers] = useState(() => {
    const saved = localStorage.getItem('activeTimers');
    if (!saved) return {};
    const parsed = JSON.parse(saved);
    const now = Date.now();

    // Remove expired timers
    const validTimers = {};
    for (const [id, endTime] of Object.entries(parsed)) {
      if (endTime > now) {
        validTimers[id] = endTime;
      }
    }
    return validTimers;
  });

  // Save to localStorage whenever timers change
  useEffect(() => {
    localStorage.setItem('activeTimers', JSON.stringify(activeTimers));
  }, [activeTimers]);

  // Timer interval: update every second and play sound if done
  useEffect(() => {
    const interval = setInterval(() => {
      setActiveTimers(prev => {
        const updated = { ...prev };
        const now = Date.now();

        for (const id in prev) {
          if (typeof prev[id] === 'number' && prev[id] <= now) {
            updated[id] = 'ready';
            if (audioRef.current) {
              audioRef.current.play().catch(() => {});
            }
          }
        }

        return updated;
      });
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  // Start or cancel a timer
  const toggleTimer = (id, duration) => {
    setActiveTimers(prev => {
      const updated = { ...prev };
      if (updated[id]) {
        delete updated[id];
      } else {
        updated[id] = Date.now() + duration * 1000;
      }
      return updated;
    });
  };

  // Get time left in seconds
  const getTimeLeft = (endTime) => {
    if (!endTime) return '';
    if (endTime === 'ready') return 'Ready!';

    const diff = endTime - Date.now();
    if (diff <= 0) return 'Done';

    const totalSeconds = Math.ceil(diff / 1000);
    if (totalSeconds < 60) {
      return `${totalSeconds}s`;
    }

    const totalMinutes = Math.floor(totalSeconds / 60);
    const hours = Math.floor(totalMinutes / 60);
    const minutes = totalMinutes % 60;

    if (hours > 0) return `${hours}h ${minutes}m`;
    return `${minutes}m`;
  };

// Modal Opener
  const openCustomTimer = (item) => {
  setCurrentItemId(item.id);
  setModalOpen(true);
};

// Sets Custom Timer
const setCustomTimer = (seconds) => {
  if (currentItemId) {
    setActiveTimers(prev => ({
      ...prev,
      [currentItemId]: Date.now() + seconds * 1000
    }));
  }
  setModalOpen(false);
  setCurrentItemId(null);
};

  return (
    <div className='container'>
      <Header/>
      <div className='core-page'>

        <SideBar/>

        <div className='main-content'>
        <audio ref={audioRef} src="/done.mp3" preload="auto" />

        <ItemGroupList
          activeTimers={activeTimers}
          toggleTimer={toggleTimer}
          getTimeLeft={getTimeLeft}
          openCustomTimer={openCustomTimer}
        />
        </div>
        
      </div>
      
      <Footer/>

          {modalOpen && (
            <div className="modal-backdrop">
              <div className="modal">
                <h3>Set Custom Timer</h3>
                <input
                  type="number"
                  placeholder="Seconds"
                  onKeyDown={(e) => {
                    if (e.key === 'Enter') {
                      const seconds = parseInt(e.target.value, 10);
                      if (!isNaN(seconds)) {
                        setCustomTimer(seconds);
                      }
                    }
                  }}
                />
                <button onClick={() => setModalOpen(false)}>Cancel</button>
              </div>
            </div>
          )}

    </div>
    
    
  );
}

export default App;
