import { useState, useEffect, useRef } from 'react';

const items = [
  { id: 1, name: 'Worn CLoth', image: '/images/crafting-worn-cloth-icon.png', duration: 10 },
  { id: 2, name: 'Cloth', image: '/images/crafting-cloth-icon.png', duration: 30 },
  { id: 3, name: 'Infused Cloth', image: '/images/crafting-t1-cloth-icon.png', duration: 60 },
  { id: 3, name: 'Mage Pants', image: '/images/armor-t1-int-legs-icon.png', duration: 240 },
  { id: 3, name: 'Mage Cloak', image: '/images/armor-t1-int-body-icon', duration: 240 },
  { id: 3, name: 'Mage Hood', image: '/images/armor-t1-int-hood-icon.png', duration: 240 },
  { id: 3, name: 'Mage Pendant', image: '/images/armor-t1-int-face-icon.png', duration: 240 }
];

function App() {
  const [activeTimers, setActiveTimers] = useState({});
  const audioRef = useRef(null);

const toggleTimer = (id, duration) => {
  if (activeTimers[id]) {
    // Timer is active, so stop it by removing it
    setActiveTimers(prev => {
      const updated = { ...prev };
      delete updated[id];
      return updated;
    });
  } else {
    // Timer is not active, so start it
    const endTime = Date.now() + duration * 1000;
    setActiveTimers(prev => ({ ...prev, [id]: endTime }));
  }
};


  useEffect(() => {
    const interval = setInterval(() => {
      setActiveTimers(prev => {
        const updated = { ...prev };
        const now = Date.now();

        for (const id in prev) {
          if (prev[id] <= now) {
            delete updated[id];
            // Play sound when timer ends
            if (audioRef.current) {
              audioRef.current.play().catch(err => {
                // Optional: handle autoplay block
                console.warn("Autoplay failed:", err.message);
              });
            }
          }
        }

        return updated;
      });
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  const getTimeLeft = (endTime) => {
    const seconds = Math.ceil((endTime - Date.now()) / 1000);
    return seconds > 0 ? `${seconds}s` : 'Done';
  };

  return (
    <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 120px)', gap: '16px', padding: '20px' }}>
      {/* hidden audio player */}
      <audio ref={audioRef} src="/done.mp3" preload="auto" />

      {items.map(item => {
        const isActive = !!activeTimers[item.id];
        return (
          <div
            key={item.id}
            onClick={() => toggleTimer(item.id, item.duration)}
            style={{
              width: '120px',
              height: '120px',
              position: 'relative',
              cursor: isActive ? 'not-allowed' : 'pointer',
              opacity: isActive ? 0.5 : 1,
              border: '2px solid #333',
              borderRadius: '8px',
              overflow: 'hidden',
              backgroundColor: '#eee',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}
          >
            <img src={item.image} alt={item.name} style={{ width: '80px', height: '80px' }} />
            {isActive && (
              <div
                style={{
                  position: 'absolute',
                  top: 0,
                  left: 0,
                  width: '100%',
                  height: '100%',
                  backgroundColor: 'rgba(0,0,0,0.5)',
                  color: 'white',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontWeight: 'bold',
                  fontSize: '18px'
                }}
              >
                {getTimeLeft(activeTimers[item.id])}
              </div>
            )}
          </div>
        );
      })}
    </div>
  );
}

export default App;
