// ItemGroupList.jsx
import ItemGroup from './ItemGroup';
import itemGroups from '../data/items'; // Correct relative path
import '../styles.css';


const ItemGroupList = ({ activeTimers, toggleTimer, getTimeLeft }) => {
  return (
    <div className="item-group-list">
      {Object.entries(itemGroups).map(([tierName, tierData]) => (
        <div key={tierName} className="tier-section">
          <h1 className="tier-title">{tierName}</h1>

          {Array.isArray(tierData) ? (
            // Handle groups like "Test" that are direct arrays
            <ItemGroup
              groupName={tierName}
              items={tierData}
              activeTimers={activeTimers}
              toggleTimer={toggleTimer}
              getTimeLeft={getTimeLeft}
            />
          ) : (
            // Handle T1, T2, etc. which are objects of groups
            Object.entries(tierData).map(([groupName, items]) => (
              <ItemGroup
                key={groupName}
                groupName={groupName}
                items={items}
                activeTimers={activeTimers}
                toggleTimer={toggleTimer}
                getTimeLeft={getTimeLeft}
              />
            ))
          )}
        </div>
      ))}
    </div>
  );
};

export default ItemGroupList;
