import React from 'react';

const ItemCard = ({ item, isActive, onClick, timeLeft }) => {
  const isReady = timeLeft === 'Ready!';

  return (
    <div
      onClick={onClick}
      style={{
        width: '80px',
        height: '80px',
        position: 'relative',
        cursor: 'pointer',
        // opacity: isReady ? 1 : isActive ? 1 : 1,
        border: '2px solid #333',
        borderRadius: '8px',
        overflow: 'hidden',
        backgroundColor: isReady ? 'rgb(132, 255, 84)' : isActive ? 'rgba(78, 5, 5, 0.28)' : 'white',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}
    >
      <img src={item.image} alt={item.name} style={{ width: '60px', height: '60px' }} />
      {isActive && (
        <div
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            width: '50%',
            height: '100%',
            backgroundColor: 'rgba(78, 5, 5, 0.47)',
            color: 'white',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontWeight: 'bold',
            fontSize: '16px'
          }}
        >
          {timeLeft}
        </div>
      )}
    </div>
  );
};

export default ItemCard;
