import '../styles.css';
import ItemCard from './ItemCard';

const Form = ({   
  item,
  customDays,
  setCustomDays,
  customHours,
  setCustomHours,
  customMinutes,
  setCustomMinutes,
  customSeconds,
  setCustomSeconds,
  onSubmit,
  onCancel
}) => {
  const handleSubmit = (e) => {
    e.preventDefault(); // prevent page reload
    onSubmit();
  };

  return (
    <div className='form-container'>
      <form onSubmit={handleSubmit}>

        {/* ItemCard */}
        {item && (
          <div style={{ padding: '10px', textAlign: 'center', pointerEvents: 'none' }}>
            <ItemCard
              item={item}
              isActive={false}
              onClick={() => {}}
              timeLeft={''}
              onHold={() => {}}
            />
          </div>
        )}

        <div className='label-group'>
          <label className='label-title'>
            Days:
            <input
              className='form-input'
              type="number"
              min="0"
              value={customDays}
              onChange={(e) => setCustomDays(e.target.value)}
            />
          </label>
        </div>

        <div className='label-group'>
          <label className='label-title'>
            Hours:
            <input
              className='form-input'
              type="number"
              min="0"
              max="23"
              value={customHours}
              onChange={(e) => setCustomHours(e.target.value)}
            />
          </label>
        </div>

        <div className='label-group'>
          <label className='label-title'>
            Minutes:
            <input
              className='form-input'
              type="number"
              min="0"
              max="59"
              value={customMinutes}
              onChange={(e) => setCustomMinutes(e.target.value)}
            />
          </label>
        </div>

        <div className='label-group'>
          <label className='label-title'>
            Seconds:
            <input
              className='form-input'
              type="number"
              min="0"
              max="59"
              value={customSeconds}
              onChange={(e) => setCustomSeconds(e.target.value)}
            />
          </label>
        </div>

        <div className='input-group'>
          <button className='btn-submit' type="submit">Submit</button>
          <button className='btn-cancel' type="button" onClick={onCancel}>Cancel</button>
        </div>
      </form>
    </div>
  );
};

export default Form;
